void test_value(double *computed_value, double true_value)
{
	const double EPSINON = 0.0000001;
	double difference = (*computed_value) - true_value;
	if ((difference < EPSINON) & (difference > -EPSINON)) {
		printf("Success!\n\n");
	}
	else {
		printf("Fail!\n\n");
	}
	return;
}

